import { Component, useState } from "react";

interface Props {
    text: string,
    isPressed: boolean
}
 
const NavigationButton: React.FunctionComponent<Props> = ({text, isPressed}) => {

    const [pressed, setPressed] = useState<boolean>(isPressed);
    
    const HandlePressed = () => {
        setPressed(!pressed);
        console.log(pressed);
    };

    return ( 
        
        <button className = 
        {
            isPressed ? 
            (
                "w-1/4 font-raleway border-t-4 border-stone-900 text-stone-900 font-bold"
            ) : (
                "w-1/4 h-30 font-raleway text-stone-300 transition ease-in-out hover:bg-stone-400 hover:text-stone-50 duration-200"
            )
        } onClick = {() => HandlePressed()}>
            {text}
        </button>
    );
}
 
export default NavigationButton;
