import React, { useEffect, useState } from 'react';
import { ShoppingCartIcon, MagnifyingGlassIcon } from '@heroicons/react/24/solid';
import NavigationButton from './NavigationButton';

import { NavigationItem } from '../Models';

interface Props { }

const NavigationBar: React.FC<Props> = () => {
	

	const _navItems: NavigationItem[] = [
		{name : "Home", href : "#", isCurrent : true},
		{name : "Products", href : "#", isCurrent : false},
		{name : "Blog", href : "#", isCurrent : false},
		{name : "About", href : "#", isCurrent : false},
	];

	const[navItems, setNavItems] = useState<NavigationItem[]>(_navItems);

	useEffect(() => {
		console.log(navItems);
	});

	return (
		<header className="shadow-lg shadow-slate-200 h-30 ">
			<div className="flex flex-row justify-between">

				<div className="ml-10">
				</div>

				<div className="text-center flex w-96"> 
				{
					navItems.map((navItem) => (
						<NavigationButton
							navItem={navItem}
							navItems={navItems}
							setNavItems={setNavItems}
							key={navItem.name}
						/>
					))
				}
				</div>

				<div className="mr-10">
					<div className="flex">
						<button className="h-30 p-3 transition ease-in-out hover:bg-stone-400 hover:text-stone-50 duration-200">
							<MagnifyingGlassIcon className="h-6 w-6 mx-3 text-black-500" />
						</button>
						<button className="h-30 p-3 transition ease-in-out hover:bg-stone-400 hover:text-stone-50 duration-200">
							<ShoppingCartIcon className="h-6 w-6 mx-3 text-black-500" />
						</button>
					</div>
				</div>
			</div>
		</header>
	);
}

export default NavigationBar;
