import { useState } from "react";
import { NavigationItem } from '../Models';

interface Props {
    navItem: NavigationItem,
    navItems: NavigationItem[],
    setNavItems : React.Dispatch<React.SetStateAction<NavigationItem[]>>;
}
 
const NavigationButton: React.FunctionComponent<Props> = ({navItem, navItems, setNavItems}) => {

    const [isPressed, setIsPressed] = useState<boolean>(navItem.isCurrent);
    


    const HandlePressed = (name : string) => {
        if (!isPressed) {
            setIsPressed(!isPressed);
            setNavItems(navItems.map((n) => {
                return {
                    name: n.name,
                    href: n.href,
                    isCurrent: n.name !== name ? !n.isCurrent : false
                }
            }));
        }
        
        console.log(navItems);
    };

    return ( 
        
        <button className = 
        {
            isPressed ? 
            (
                "w-1/4 font-raleway border-t-4 border-stone-900 text-stone-900 font-bold"
            ) : (
                "w-1/4 h-30 font-raleway text-stone-300 transition ease-in-out hover:bg-stone-400 hover:text-stone-50 duration-200"
            )
        } onClick = {() => HandlePressed(navItem.name)}>
            {navItem.name}
        </button>
    );
}
 
export default NavigationButton;
